import React, { useState, useContext } from 'react';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormHelperText from '@material-ui/core/FormHelperText';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import InfoAdornment from './InfoAdornment';
import { infoText } from '../utilities/constants';

import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormLabel from '@material-ui/core/FormLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';


const useStyles = makeStyles((theme) => ({
	stateInputLabel: {
		color: "rgba(0,0,0,0.87)",
	},
}));

const StateSelect = () => {
    const inputContext = useContext(InputParamsContext);
    const [postals, setPostals] = useState(inputContext.inputParams.state);
    const classes = useStyles();

    const handleChange = (event) => {
		const newPostals = event.target.value;
		setPostals(newPostals);
		inputContext.updateInputParams({state:newPostals});	
	};

    return (
        <>
            <FormControl variant="outlined">
                <InputLabel htmlFor="stateInput" className={classes.stateInputLabel}>State(s)</InputLabel>
                <OutlinedInput
                    value={postals}
                    id="stateInput"
                    label="State(s)"
                    margin="dense"
                    onChange={handleChange}
                    endAdornment={<InfoAdornment content={infoText.postal} />}
                />
                <FormHelperText id="stateInput">Enter one or more state postal abbreviations</FormHelperText>
            </FormControl>

            <FormControl component="fieldset" className={classes.formControl}>
                <FormLabel component="legend">State options:</FormLabel>
                <RadioGroup row aria-label="area type" name="areatype" value={areaType} onChange={handleTypeChange}>
                    <FormControlLabel value="all" control={<Radio size="small" className={classes.radioButton} />} label="Entire state" />
                    <FormControlLabel value="county" control={<Radio disabled size="small" className={classes.radioButton} />} label="County" />
                    <FormControlLabel value="climdiv" control={<Radio disabled size="small" className={classes.radioButton} />} label="Climate division" />
                </RadioGroup>
            </FormControl>

            {areaType === 'county' &&
                <p>To be implemented</p>
            }
        </>
    );

 }

export default StateSelect;
