import React, { useState, useContext } from 'react';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormLabel from '@material-ui/core/FormLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';

import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';


const useStyles = makeStyles((theme) => ({
	formControl: {
		marginTop: "1em",
	},
	radioButton: {
		color: "midnightblue !important",
		paddingRight: "3px",
		fontSize: "90%",
	},
	vertRadioButton: {
		color: "midnightblue !important",
		padding: "6px",
		fontSize: "90%",
	},
}));

const SubstateSelect = (props) => {
	const { postals } = props;
	const inputContext = useContext(InputParamsContext);
	const [stateType, setStateType] = useState('all');
	const classes = useStyles();

	const handleTypeChange = (event) => {
		setStateType(event.target.value);
	};

	return (
		<>
			<Grid container spacing={1}>
				<Grid item>
					<FormControl component="fieldset" className={classes.formControl}>
						<FormLabel component="legend">State options:</FormLabel>
						<RadioGroup column aria-label="state type" name="statetype" value={stateType} onChange={handleTypeChange}>
							<FormControlLabel 
								value="all" 
								control={<Radio size="small" className={classes.vertRadioButton} />} 
								label="Entire area" 
							/>
							<FormControlLabel 
								value="county" 
								control={<Radio size="small" className={classes.vertRadioButton}/>} 
								label="County" 
							/>
							<FormControlLabel 
								value="climdiv" 
								control={<Radio disabled size="small" className={classes.vertRadioButton} />} 
								label="Climate division" 
							/>
						</RadioGroup>
					</FormControl>
				</Grid>

				<Grid>
			{stateType === 'county' &&
				"To be implemented"
			}
					<List aria-label="Element selection">
						<ListItem
							button
							aria-haspopup="true"
							aria-controls="element-menu"
							aria-label="selected element"
						>
							<ListItemText primary="Click to select element" secondary={"Currently selected: "} />
						</ListItem>
					</List>
				</Grid>
			</Grid>

		</>
	);
 };

export default SubstateSelect;

