import React, {useState} from 'react';
import clsx from 'clsx';
import Banner from './Banner';
import OptionsSelection from './OptionsSelection';
import GetImage from './GetImage';
import { makeStyles } from '@material-ui/core/styles';

const drawerWidth = 400;

const useStyles = makeStyles((theme) => ({
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: 0,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: drawerWidth,
  },
}));

var input_params = {
  "grid":"nrcc-nn",
  "state":"ME,WV",
  "sdate":"2020-01",
  "edate":"2020-01",
  "elems":[{
    "name":"pcpn",
    "interval":[0,1],
    "duration":1,
    "reduce":"sum"
  }],
  "output":"gif"
};

const Main = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(true);
  const [viewMap, setViewMap] = useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleViewMapClick = () => {
    setViewMap(true);
  };

  return (
    <InputParamsContext.Provider value={{ input_params }}>
    <>
      <Banner
        open={open}
        handleDrawerOpen={handleDrawerOpen}
      />

      <nav>
        <OptionsSelection
          open={open}
          handleDrawerClose={handleDrawerClose}
          handleViewMapClick={handleViewMapClick}
        />
      </nav>

      <main>
        <div className={clsx(classes.content, { [classes.contentShift]: open, })}>
          <div className={classes.drawerHeader}  />
          {viewMap &&
            <GetImage />
          }
          {!viewMap &&
            <>
              <p>... Introductory text here ... Set options and click "View Map" button.</p>
            </>
          }
        </div>
      </main>
    </>
    </InputParamsContext.Provider>
  )
}

export default Main;
