import { elements } from '../utilities/constants';

//const dateDiff = (date1, date2) => {
//	const sdate = date1 instanceof Date ? date1 : new Date(date1);
//	const edate = date2 instanceof Date ? date2 : new Date(date2);
//	const one_day = 1000 * 60 * 60 * 24;
//	return (Math.round((edate.getTime() - sdate.getTime()) / one_day));
//};

const buildParams = (input_params) => {
	let paramsToSubmit = {grid: input_params.grid, output: input_params.output};
	console.log(input_params) // for debugging
	// For "interval" date selection, calculate duration based on sdate and edate. Also send just date instead of sdate and edate.
	const newElems = [...input_params.elems];
	if (input_params.elems[0].duration !== 1) {
		const mndiff = parseInt(input_params.edate.split("-")[1]) - parseInt(input_params.sdate.split("-")[1]);
		const duration = mndiff + (mndiff >= 0 ? 1 : 13);
		console.log(mndiff,duration)
		input_params.elems[0].duration = duration;
		newElems[0] = {...newElems[0], duration};
	}

	if (newElems[0].name === 'all') {
		newElems[0] = {...newElems[0], name: 'maxt', reduce: elements[0].reduce};
		['mint','avgt','pcpn'].forEach((elem,i) => {
			newElems.push({...newElems[0], name:elem, reduce: elements[i+1].reduce});
		});
	}
	paramsToSubmit.elems = newElems;

	const newDate = input_params.elems[0].interval.length === 2 ? input_params.edate.slice(0, 7) : input_params.edate;
	paramsToSubmit.date = newDate;

	// areaDef is type (e.g. state, loc, county) and value of area of interest.
	paramsToSubmit = {...paramsToSubmit, ...input_params.areaDef};

	// point location can only be json
	if (paramsToSubmit.loc) {
		paramsToSubmit = {...paramsToSubmit, output: "json"};
	}

	return paramsToSubmit;	
};

export { buildParams };
