import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
    },
    appBar: {
      maxWidth: document.body.clientWidth,  // Added to prevent appbar from extending beyond body width - KLE
      right: 'auto',                        // ditto
      transition: theme.transitions.create(['margin', 'width'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      backgroundColor:"midnightblue",
    },
    appBarShift: {
      width: `calc(${document.body.clientWidth}px - ${drawerWidth}px)`, // Also made this clientWidth
      marginLeft: drawerWidth,
      transition: theme.transitions.create(['margin', 'width'], {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
    },
    menuButton: {
      marginRight: theme.spacing(2),
      "&:focus": {
        color: "#3f51b5",
        backgroundColor: "lightblue",
      },
      "&:hover": {
        color: "#3f51b5",
        backgroundColor: "lightblue",
      },
    },
    hide: {
      display: 'none',
    },
    drawer: {
      width: drawerWidth,
      flexShrink: 0,
    },
    drawerPaper: {
      width: drawerWidth,
    },
    drawerHeader: {
      display: 'flex',
      alignItems: 'center',
      padding: theme.spacing(0, 1),
      // necessary for content to be below app bar
      ...theme.mixins.toolbar,
      justifyContent: 'flex-end',
    },
    content: {
      flexGrow: 1,
      padding: theme.spacing(0),  // changed from 3
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      marginLeft: 0, // example had this as -drawerWidth
    },
    contentShift: {
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: drawerWidth,  // example had this as 0
    },
    tabPanel: {
      padding: "1rem",
    },
    styledTab: {
      textTransform: 'capitalize',
      fontSize: '1.0rem',
      minHeight: '2.25rem',
      padding: '0',
      "&:focus": {
        backgroundColor: "lightblue",
      },
      "&:hover": {
        backgroundColor: "lightblue",
      },
    },
  }));

  export default useStyles;
