import React, { useState, useContext } from 'react';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormHelperText from '@material-ui/core/FormHelperText';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import InfoAdornment from './InfoAdornment';

const useStyles = makeStyles((theme) => ({
	stateInputLabel: {
		color: "rgba(0,0,0,0.87)",
		fontSize: "1.2em",
	},
}));

const StateSelect = () => {
    const inputContext = useContext(InputParamsContext);
    const [postals, setPostals] = useState(inputContext.inputParams.state);
    const classes = useStyles();

    const infoText = 'A list of state abbreviations will return an area emcompassing all states in the list. For example, "ME,WV" will return the Northeast (i.e. Maine to West Virginia).';

    const handleChange = (event) => {
		const newPostals = event.target.value;
		setPostals(newPostals);
		inputContext.updateInputParams({state:newPostals});	
	};

    return (
        <FormControl variant="outlined">
            <InputLabel htmlFor="stateInput" className={classes.stateInputLabel}>State(s)</InputLabel>
            <OutlinedInput
                value={postals}
                id="stateInput"
                label="State(s)"
                onChange={handleChange}
                endAdornment={<InfoAdornment content={infoText} />}
            />
            <FormHelperText id="stateInput">Enter one or more state postal abbreviations</FormHelperText>
        </FormControl>
        );
    }

export default StateSelect;
