import React, { useState } from 'react';
import Typography from '@material-ui/core/Typography';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import { makeStyles } from '@material-ui/core/styles';
import RenderDateSelect from './RenderDateSelect';

const useStyles = makeStyles((theme) => ({
	dateTypeContainer: {
		borderLeft: "1pt solid midnightblue",
		borderRight: "1pt solid midnightblue",
		borderBottom: "1pt solid midnightblue",
		borderTop: "0",
		padding: "1em 1em",
	},
	radioButton: {
		color: "midnightblue !important",
		paddingRight: "3px",
	},
	formControl: {
		marginTop: "0.5em",
	},
	filterLabel: {
		display: "inline-block",
		lineHeight: "4.4em",
		verticalAlign: "bottom",
		marginRight: "0.25em",
		marginLeft: "0.25em",
	},
}));

const DateSelect = () => {
    const [dateType, setDateType] = useState('day');
	const classes = useStyles();

    const handleTypeChange = (event) => {
        setDateType(event.target.value);
    };

	return (
		<div className={classes.dateTypeContainer}>
			<Typography variant="body1">
				Date selection
			</Typography>

            <FormControl component="fieldset" className={classes.formControl}>
            <FormLabel component="legend">Date type:</FormLabel>
            <RadioGroup row aria-label="date type" name="datetype" value={dateType} onChange={handleTypeChange}>
                <FormControlLabel value="day" control={<Radio className={classes.radioButton} />} label="Day" />
                <FormControlLabel value="month" control={<Radio className={classes.radioButton} />} label="Month" />
                <FormControlLabel value="interval" control={<Radio className={classes.radioButton} />} label="Interval" />
            </RadioGroup>
            </FormControl>

            {dateType === 'interval' &&
				<div>
					<RenderDateSelect 
						dateLegend='Start date'
						dateParam='sdate'
					/>
					<Typography className={classes.filterLabel}>
						-
					</Typography>
					<RenderDateSelect 
						dateLegend='End date'
						dateParam='edate'
					/>
				</div>
		}
		</div>
	)
}

export default DateSelect;
