import React, { useEffect, useState, useContext} from "react";
import CircularProgress from "@material-ui/core/CircularProgress";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import InputParamsContext from './InputParamsContext';
import { buildParams } from '../utilities/utils';
import MapTitle from './MapTitle';
import DisplayMap from "./DisplayMap";
import DisplayJson from "./DisplayJson";

const useStyles = makeStyles((theme) => ({
	preContainer: {
		display:"inline-block", 
		padding:"0 1em",
		margin: "0 0 0.5em",
		backgroundColor: "whitesmoke",
	},
	preStyle: {
		marginTop: 0,
		fontSize: "90%",
	},
}));

const GetResults = () => {
	const [mapBlob, setMapBlob] = useState();
	const [jsonresp, setJsonresp] = useState();
	const [resultsError, setResultsError] = useState();
	const [loading, setLoading] = useState(false);
	const [submittedParams, setSubmittedParams] = useState();
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	const fetchResults = (jsonparams, output) => {
		fetch("https://grid2.rcc-acis.org/GridData", {body: jsonparams, method: "POST"})
			.then(response => {
				if (!response.ok) {
					setResultsError("Error fetching the results");
					return;
				}
				return output === 'gif' ? response.blob() : response.json();
			})
			.then(response => {
				if (output === 'gif') {
					setMapBlob(URL.createObjectURL(response));
				} else {
					setJsonresp(response.data);
				}
			})
			.catch(err => {
				if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
					setResultsError("Invalid grid request - "+jsonparams);
				} else {
					setResultsError(err.message);
				};
			})
			.finally(() => {
				setLoading(false);
			});
	};

	useEffect(() => {
		setLoading(true);
		// Build parameters
		const input = buildParams(inputContext.inputParams);
		const jsonparams = JSON.stringify(input, null, 2);
		setSubmittedParams(jsonparams);
		// Get results
		fetchResults(jsonparams, input.output);
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [inputContext.inputParams]);

	return (
		<>
			{loading &&
				<CircularProgress />
			}
			{submittedParams && (jsonresp || mapBlob) &&
				<>
					<MapTitle />
					{mapBlob &&
						<DisplayMap 
							imgsrc={mapBlob}
						/>
					}
					{jsonresp &&
						<DisplayJson
							jsonresp={jsonresp}
							submittedParams={submittedParams}
						/>
					}
					<Paper variant="outlined" className={classes.preContainer}>
						<Typography variant="subtitle1">
                   			Request:
                		</Typography>
						<pre className={classes.preStyle}>
							{submittedParams}
						</pre>
					</Paper>
				</>
			}
			{resultsError &&
				<p>Error: {resultsError}</p>
			}
		</>
	);
}

export default GetResults;
