import React, {useEffect, useState} from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import Banner from './Banner';
import OptionsSelection from './OptionsSelection';
import GetResults from './GetResults';
import InputParamsContext from './InputParamsContext';
import { default_params, drawerWidth } from '../utilities/constants';
import poweredByAcis from '../images/PoweredbyACIS.gif';

const useStyles = makeStyles((theme) => ({
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(2),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: 0,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: drawerWidth,
  },
  footerContainer: {
    position: "fixed", 
    width: "calc(100% - " + drawerWidth + "px - 2em)", 
    bottom: 0, 
    marginTop: "1em", 
    borderTop: "1pt solid midnightblue",
    textAlign: "right",
    padding: "0.5em 0",
  },
}));

const Main = () => {
  const classes = useStyles();
  const [viewMap, setViewMap] = useState(false);
  const [inputParams, setInputParams] = useState(default_params)
  const theme = useTheme();
	const smallScreen = useMediaQuery(theme.breakpoints.down('sm'))
  const [open, setOpen] = useState(!smallScreen);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleViewMapClick = () => {
    setViewMap(true);
    if (smallScreen) {
      setOpen(false);
    }
  };

  const updateInputParams = (updateParams) => {
    setViewMap(false);
    const newParams = {...inputParams, ...updateParams}
    setInputParams(newParams);
  }

  useEffect(() => {
    // initial useState does not recognize small screen yet; so need to set drawer status here
    setOpen(!smallScreen);
  },[smallScreen]);

  return (
    <InputParamsContext.Provider value={{ inputParams, updateInputParams }}>
      <>
        <a href="#maincontent" className="skip">Skip to main content</a>

        <Banner
          open={open}
          handleDrawerOpen={handleDrawerOpen}
        />

        <nav>
          <OptionsSelection
            open={open}
            handleDrawerClose={handleDrawerClose}
            handleViewMapClick={handleViewMapClick}
          />
        </nav>

        <main id="maincontent">
          <div className={classes.drawerHeader}  />
          <div className={clsx(classes.content, { [classes.contentShift]: open, })}>
            {viewMap &&
              <GetResults />
            }
            {!viewMap &&
              <>
                <Typography paragraph color="primary" align="center" style={{border:"1px solid darkblue",padding:"1em",margin:"1em 5em"}}>
                  This is a beta release of the NCEI Normals Mapper. Some features, such as map placement near the US borders 
                  and a map legend are not available at this time. A more complete release is expected around May 14.
                </Typography> 

                <Typography paragraph>
                  Climate Normals are three-decade averages of climatological variables including temperature and precipitation. 
                  These normals are updated once every 10 years. This initial release of 
                  the 
                  1991-2020 gridded <a href="https://www.ncei.noaa.gov/products/us-climate-normals" target="_blank" rel="noreferrer"> U.S. climate normals</a>  
                  dataset from the <a href="https://www.ncei.noaa.gov/" target="_blank" rel="noreferrer">National Centers for Environmental Information</a> contains monthly normals of temperature (maximum, miminum, average) and precipitation for the conterminous 
                  United States.
                </Typography>

                <Typography>
                  To view the data contained in the gridded climate normals dataset, set your desired options in the
                  navigation pane and click the "Submit Request" button to view the results. Buttons will be provided below each
                  resulting product to allow you to download the data in a variety of formats.
                </Typography>

                <footer className={classes.footerContainer}>
                    <img src={poweredByAcis} alt={'Powered by ACIS'} />
                </footer>
              </>
            }
          </div>
        </main>
      </>
    </InputParamsContext.Provider>
  )
}

export default Main;
