import React, { useState, useContext } from 'react';
import Typography from '@material-ui/core/Typography';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormLabel from '@material-ui/core/FormLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import InputLabel from '@material-ui/core/InputLabel';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import RegionSelect from './RegionSelect';

const useStyles = makeStyles((theme) => ({
	areaContainer: {
		borderLeft: "1pt solid midnightblue",
		borderRight: "1pt solid midnightblue",
		borderBottom: "1pt solid midnightblue",
		borderTop: "0",
		padding: "1em 1em",
	},
	radioButton: {
		color: "midnightblue !important",
		paddingRight: "3px",
	},
	formControl: {
		marginTop: "0.5em",
		marginBottom: "1em",
	},
	stateInputLabel: {
		color: "rgba(0,0,0,0.87)",
		fontSize: "1.2em",
	},
}));

const AreaSelect = () => {
	const inputContext = useContext(InputParamsContext);
	const [postals, setPostals] = useState(inputContext.inputParams.state);
	const [areaType, setAreaType] = useState("state");
	const classes = useStyles();

	const handleTypeChange = (event) => {
		const newAreaType = event.target.value;
		setAreaType(newAreaType);
	};

	const handleChange = (event) => {
		const newPostals = event.target.value;
		setPostals(newPostals);
		inputContext.updateInputParams({state:newPostals});	
	};

	return (
		<div className={classes.areaContainer}>
			<Typography variant="body1">
				Select area
			</Typography>

			<FormControl component="fieldset" className={classes.formControl}>
				<FormLabel component="legend">Area type:</FormLabel>
				<RadioGroup row aria-label="area type" name="areatype" value={areaType} onChange={handleTypeChange}>
					<FormControlLabel value="state" control={<Radio size="small" className={classes.radioButton} />} label="State" />
					<FormControlLabel value="region" control={<Radio size="small" className={classes.radioButton} />} label="Region" />
					<FormControlLabel value="bbox" control={<Radio disabled size="small" className={classes.radioButton} />} label="Bounding Box" />
				</RadioGroup>
			</FormControl>

			{areaType === 'state' &&
				<FormControl variant="outlined">
					<InputLabel htmlFor="stateInput" className={classes.stateInputLabel}>State(s)</InputLabel>
					<OutlinedInput
						value={postals}
						id="stateInput"
						label="State(s)"
						onChange={handleChange}
					/>
					<FormHelperText id="stateInput">Enter one or more state postal abbreviations</FormHelperText>
				</FormControl>
			}
			{areaType === 'region' &&
				<RegionSelect />
			}
		</div>
	);
}

export default AreaSelect;
