import React, { useState, useContext } from 'react';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const monthChoices = ['January','February','March','April','May','June','July','August','September','October','November','December'];
const dayMonths30 = ['04','06','09','11'];

const useStyles = makeStyles((theme) => ({
	dateRangeContainer: {
		borderLeft: "1pt solid midnightblue",
		borderRight: "1pt solid midnightblue",
		borderBottom: "1pt solid midnightblue",
		borderTop: "0",
		padding: "1em 1em",
	},
	filterLabel: {
		display: "inline-block",
		lineHeight: "4.4em",
		verticalAlign: "bottom",
		marginRight: "0.25em",
		marginLeft: "0.25em",
	},
	filterGroup: {
		display: "inline-block",
		border: "1px solid rgba(0, 0, 0, 0.23)",
		borderRadius: "4px",
		padding: "0 0.6em",
		margin: "0.6em 0 0.6em 0",
	},
	filterGroupLegend: {
		fontSize: "95%",
	},
	textfield: {
    marginRight: "0.5em",
	},
	menuitem: {
		paddingTop: "1px",
		paddingBottom: "1px",
	}
}));

const DateRange = () => {
	const [fromFilterMonth, setFromFilterMonth] = useState('02');
	const [fromFilterDay, setFromFilterDay] = useState('02');
	const [toFilterMonth, setToFilterMonth] = useState('12');
	const [toFilterDay, setToFilterDay] = useState('31');
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	const updateDate = (se, newMonth, newDay) => {
		if (se === 'sdate' || se === 'edate') {
			const newdate = ["2020", newMonth, newDay].join("-");
			inputContext.updateInputParams(se, newdate);	
		}
	};

	const handleChangeMonth = (name) => event => {
		const newMonth = event.target.value;
		if (name === 'fromFilterMonth') {
			setFromFilterMonth(newMonth);
			if (fromFilterDay === '31' && dayMonths30.includes(newMonth)) {
				setFromFilterDay('30');
				updateDate('sdate', newMonth, '30');
			} else if (fromFilterDay >= '30' && event.target.value === '02') {
				setFromFilterDay('29');
				updateDate('sdate', newMonth, '29');
			} else {
				updateDate('sdate', newMonth, fromFilterDay);
			};
		} else if (name === 'toFilterMonth') {
			setToFilterMonth(newMonth)
			if (toFilterDay === '31' && dayMonths30.includes(newMonth)) {
				setToFilterDay('30');
				updateDate('edate', newMonth, '30');
			} else if (toFilterDay >= '30' && event.target.value === '02') {
				setToFilterDay('29');
				updateDate('edate', newMonth, '29');
			} else {
				updateDate('edate', newMonth, toFilterDay);
			}
		}
	};

	const handleChangeDay = (name) => event => {
		const newDay = event.target.value;
		if (name === 'fromFilterDay') {
			setFromFilterDay(newDay);
			updateDate('sdate', fromFilterMonth, newDay);
		} else if (name === 'toFilterDay') {
			setToFilterDay(newDay);
			updateDate('edate', toFilterMonth, newDay);
		}
	};

	const serialMonthOptions = (fromto) => {
    return (
      monthChoices.map((option,i) => {
        return (
					<MenuItem 
						key={"m"+fromto+i} 
						className={classes.menuitem}
						value={("0" + (i+1)).slice(-2)}
					>
            {option}
          </MenuItem>
        )
      })
    );
	};	

	const serialDayOptions = (fromto) => {
		let arrlen = 31;
		if ((fromto === 'from' && dayMonths30.includes(fromFilterMonth)) || (fromto === 'to' && dayMonths30.includes(toFilterMonth)) ) {
			arrlen = 30;
		} else if ((fromto === 'from' && fromFilterMonth === '02') || (fromto === 'to' && toFilterMonth === '02')) {
			arrlen = 29;
		}
    return (
      Array.from({length: arrlen}, (v, k) => k+1).map((option,i) => {
        return (
					<MenuItem 
						key={"d"+fromto+i} 
						className={classes.menuitem}
						value={("0" + (i+1)).slice(-2)}
					>
            {option}
          </MenuItem>
        )
      })
    );
  };

	return (
		<div className={classes.dateRangeContainer}>
			<Typography variant="body1">
				Select date range
			</Typography>
			<fieldset className={classes.filterGroup}>
				<legend className={classes.filterGroupLegend}>Start date</legend>
				<TextField
					select
					id="fromFilterMonth"
					value={fromFilterMonth}
					onChange={handleChangeMonth('fromFilterMonth')}
					className={classes.textfield}
					margin="dense"
					SelectProps={{ native: false }}
				>
					{serialMonthOptions('from')}
				</TextField>
				<TextField
					select
					id="fromFilterDay"
					value={fromFilterDay}
					onChange={handleChangeDay('fromFilterDay')}
					margin="dense"
					SelectProps={{ native: false }}
					>
						{serialDayOptions('from')}
				</TextField>
			</fieldset>
			<Typography className={classes.filterLabel}>
				 -
			</Typography>
			<fieldset className={classes.filterGroup}>
				<legend className={classes.filterGroupLegend}>End date</legend>
				<TextField
					select
					id="toFilterMonth"
					value={toFilterMonth}
					onChange={handleChangeMonth('toFilterMonth')}
					className={classes.textfield}
					margin="dense"
					SelectProps={{ native: false }}
					>
						{serialMonthOptions('to')}
				</TextField>
				<TextField
					select
					id="toFilterDay"
					value={toFilterDay}
					onChange={handleChangeDay('toFilterDay')}
					margin="dense"
					SelectProps={{ native: false }}
					>
						{serialDayOptions('to')}
				</TextField>
			</fieldset>
		</div>
	)
}

export default DateRange;
