import React, { useEffect, useRef, useState } from "react";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableRow from '@material-ui/core/TableRow';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
	table: {
		width:"9.2em",
	},
	range: {
		width:"8em",
	},
}));

const MapLegend = (props) => {
	const { imgInfo } = props;
	const { levels, cmap } = imgInfo;
	const [ width, setWidth ] = useState();
	const [ eachWidth, setEachWidth ] = useState();
	const classes = useStyles();
	const ref = useRef(null);

	let color_styles = [];
	let color_styles2 = [];
	cmap.forEach((color) => {
		color_styles.push( { width: "1.2em", backgroundColor: color, opacity: "0.8"} );
		color_styles2.push( {height: "28px", backgroundColor: color, opacity: "0.8"} );
	})

	useEffect(() => {
		if (ref) {
		const elemWidth = ref.current.offsetWidth;
		const defaultWidth = cmap.length * 56;
		setWidth(Math.min(defaultWidth, elemWidth));
		setEachWidth(Math.min(defaultWidth, elemWidth)/levels.length);
		}
	});

	return (
		<>

		<TableContainer ref={ref}>
			<Table size="small" padding="none" style={{width:width}} aria-label="color table">
				<TableBody>
					<TableRow>
						{color_styles2.map((cs, i) => (
							<TableCell style={cs}></TableCell>
						))}
					</TableRow>	
				</TableBody>
			</Table>
		</TableContainer>

		<TableContainer>
			<Table size="small" padding="none" style={{width:width}} aria-label="color table">
				<TableBody>
					<TableRow>
						{levels.map((lv, i) => (
							<>
							<TableCell style={{width:eachWidth/3}}></TableCell>
							<TableCell align="right" style={{width:eachWidth/3}}>{lv}</TableCell>
							<TableCell style={{width:eachWidth/3}}></TableCell>
							</>
							))}
							<TableCell style={{width:eachWidth/3}}></TableCell>
							<TableCell style={{width:eachWidth/3}}></TableCell>
					</TableRow>	
				</TableBody>
			</Table>
		</TableContainer>

		</>



	);
}

export default MapLegend;

