import React, { useState, useContext } from 'react';
import Typography from '@material-ui/core/Typography';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormHelperText from '@material-ui/core/FormHelperText';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const useStyles = makeStyles((theme) => ({
	areaContainer: {
		borderLeft: "1pt solid midnightblue",
		borderRight: "1pt solid midnightblue",
		borderBottom: "1pt solid midnightblue",
		borderTop: "0",
		padding: "1em 1em",
	},
}));

const AreaSelect = () => {
	const inputContext = useContext(InputParamsContext);
	const [postals, setPostals] = useState(inputContext.inputParams.state);
	const classes = useStyles();

	const handleChange = (event) => {
		const newPostals = event.target.value;
		setPostals(newPostals);
		inputContext.updateInputParams({state:newPostals});	
	};

	return (
		<div className={classes.areaContainer}>
			<Typography variant="body1">
				Select area
			</Typography>
			<p>Bounding box, region, state, drag rectangle, etc.</p>

			<OutlinedInput
				value={postals}
				id="stateInput"
				label="State(s)"
				defaultValue={postals}
				helperText="Enter one or more state postal abbreviations"
				variant="outlined"
				autofocus={true}
				margin="dense"
				onChange={handleChange}
			/>
			<FormHelperText id="stateInput">State(s)</FormHelperText>



		</div>
	);
}

export default AreaSelect;

