import React, { useState, useEffect, useContext } from 'react';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormHelperText from '@material-ui/core/FormHelperText';
import InfoAdornment from './InfoAdornment';
import InputParamsContext from './InputParamsContext';
import { infoText } from '../utilities/constants';

const useStyles = makeStyles((theme) => ({
	imageOptionsContainer: {
		borderLeft: "1pt solid midnightblue",
		borderRight: "1pt solid midnightblue",
		borderBottom: "1pt solid midnightblue",
		padding: 0,
	},
	accordionDetailsContainer: {
		paddingTop: 0,
	},
	accordianSummaryRoot: {
		minHeight: "1em",
		marginTop: "1em",
		marginBottom: "1em",
		'&$expanded': {
			minHeight: "1em",
			marginBottom: "0.5em",
		},
	},
	accordianSummaryContent: {
		margin: 0,
		padding:  0,
		'&$expanded': {
			margin: 0,
		},
	},
	expanded: {},
	expandIcon: {
		paddingTop: 0,
		paddingBottom: 0,
	},
	vertCheckbox: {
		color: "midnightblue !important",
		padding: "6px",
		fontSize: "90%",
	},
	levelInputLabel: {
		color: "rgba(0,0,0,0.87)",
	},
}));

const ImageSelect = () => {
	const [ stateOutlines, setStateOutlines ] = useState(true);
	const [ countyOutlines, setCountyOutlines ] = useState(true);
	const [ levels, setLevels ] = useState("");
	const classes = useStyles();
	const inputContext = useContext(InputParamsContext);

	const handleLevelChange = (event) => {
		const newlevels = event.target.value.replace(" ","");;
		setLevels(newlevels);
		inputContext.updateInputParams({image: {...inputContext.inputParams.image, levels: newlevels}});
	};

	const handleCheckboxChange = (event) => {
		if (event.target.name === 'stateOutlines') {
			setStateOutlines(event.target.checked);
		} else if (event.target.name === 'countyOutlines') {
			setCountyOutlines(event.target.checked);
		}
	};

	useEffect(() => {
		const newOverlays = [
			stateOutlines ? "state:1:black" : "state:0:black",
			countyOutlines ? "county:0.5:grey" : "county:0:grey"
		];
		inputContext.updateInputParams({image: {...inputContext.inputParams.image, overlays: newOverlays}});	
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [stateOutlines, countyOutlines]);

	useEffect(() => {
		if (inputContext.levels.server) {
			const newlevels = inputContext.levels.server.join(",");
			setLevels(newlevels);	
		}
	}, [inputContext.levels.server]);

	return ( 
		<div className={classes.imageOptionsContainer}>
			<Accordion>
				<AccordionSummary
					expandIcon={<ExpandMoreIcon />}
					aria-controls="image-options-content"
					id="image-options-header"
					classes={{root: classes.accordianSummaryRoot, content: classes.accordianSummaryContent, expanded: classes.expanded, expandIcon: classes.expandIcon}}
				>
					<Typography variant="body1">Image Settings</Typography>
				</AccordionSummary>
				<AccordionDetails
					className={classes.accordionDetailsContainer}
				>
					<FormControl component="fieldset" style={{width:"100%"}}>
						<FormControlLabel
							control={
								<Checkbox size="small" checked={stateOutlines} className={classes.vertCheckbox} onChange={handleCheckboxChange} name="stateOutlines" />
							}
							label="Show state outlines"
						/>
						<FormControlLabel
							control={
								<Checkbox size="small" checked={countyOutlines} className={classes.vertCheckbox} onChange={handleCheckboxChange} name="countyOutlines" />
							}
							label="Show county outlines"
						/>

						<FormControl variant="outlined" style={{marginTop: "0.5em"}}>
							<InputLabel htmlFor="levelInput" className={classes.levelInputLabel}>Contour levels</InputLabel>
							<OutlinedInput
								value={levels}
								id="levelInput"
								label="Contour levels"
								margin="dense"
								inputProps={{ spellCheck: false }}
								onChange={handleLevelChange}
								endAdornment={<InfoAdornment content={infoText.levels} />}
							/>
							<FormHelperText>Comma-separated contour level list; lowest to highest</FormHelperText>
						</FormControl>
					</FormControl>
				</AccordionDetails>
			</Accordion>
		</div>
	);
}

export default ImageSelect;
