import React, { useEffect, useState, useContext } from 'react';
import { MapContainer, TileLayer, Polygon, ImageOverlay } from 'react-leaflet'
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import { multiStateBbox } from '../utilities/utils';

const useStyles = makeStyles((theme) => ({
	nav: {
		position: "absolute",
		top: 0,
		left: 0,
		padding: "3px",
	},
	mapContainer: {
		border: "1pt solid midnightblue",
		height: window.innerHeight * 0.6,
	},
}));

const LeafletMap = (props) => {
	const { imgsrc, dataBbox } = props;
	const [ bbox, setBbox ] = useState();
	const [ imgcoords, setImgcoords ] = useState();
	const classes = useStyles();

	useEffect(() => {
		if (dataBbox) {
			// setup bounding box
			setBbox([parseFloat(dataBbox[0]),parseFloat(dataBbox[1]),parseFloat(dataBbox[2]),parseFloat(dataBbox[3])]);
			// convert bbox to Leaflet coordinates
			setImgcoords([ [dataBbox[3],dataBbox[0]], [dataBbox[3],dataBbox[2]], [dataBbox[1],dataBbox[2]], [dataBbox[1],dataBbox[0]] ]);
		}
		// eslint-disable-next-line 
	}, [dataBbox]);

	return (
		<>
			<div className={classes.mapContainer}>
				{bbox && 
					<MapContainer 
						bounds={[[bbox[1], bbox[0]], [bbox[3], bbox[2]]]}
						zoomControl={true}
						scrollWheelZoom={true}
						attributionControl={true}
					>
						<TileLayer
							url='https://{s}.basemaps.cartocdn.com/light_nolabels/{z}/{x}/{y}{r}.png'
							attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>'
						/>

						{imgcoords &&
							<Polygon 
								pathOptions={{color:"lightgray", weight:1, fill:false}}
								positions={imgcoords} 
							/>
						}

						{imgcoords && imgsrc &&
							<ImageOverlay
								url={imgsrc}
								bounds={imgcoords}
								opacity={0.8}
								zIndex={10}
							/>
						}

					</MapContainer>
				}
			</div>
			{resultsError &&
				<p>Error: {resultsError}</p>
			}
		</>
	);
}

export default LeafletMap;
