import React, {useState} from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Drawer from '@material-ui/core/Drawer';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import Banner from './Banner';
import GetImage from './GetImage';

const drawerWidth = 400;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    maxWidth: document.body.clientWidth,  // Added to prevent appbar from extending beyond body width - KLE
    right: 'auto',                        // ditto
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    backgroundColor:"midnightblue",
  },
  appBarShift: {
    width: `calc(${document.body.clientWidth}px - ${drawerWidth}px)`, // Also made this clientWidth
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
    "&:focus": {
      color: "#3f51b5",
      backgroundColor: "lightblue",
    },
    "&:hover": {
      color: "#3f51b5",
      backgroundColor: "lightblue",
    },
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(0),  // changed from 3
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: 0, // example had this as -drawerWidth
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: drawerWidth,  // example had this as 0
  },
  tabPanel: {
    padding: "1rem",
  },
  styledTab: {
    textTransform: 'capitalize',
    fontSize: '1.0rem',
    minHeight: '2.25rem',
    padding: '0',
    "&:focus": {
      backgroundColor: "lightblue",
    },
    "&:hover": {
      backgroundColor: "lightblue",
    },
  },
}));

const Main = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(true);
  const [getMap, setGetMap] = useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleGetMapClick = () => {
    setGetMap(true);
  }

  return (
    <>
      <Banner
        classes={classes}
        handleDrawerOpen={handleDrawerOpen}
      />

      <nav>
        <Drawer
          className={classes.drawer}
          variant="persistent"
          anchor="left"
          open={open}
          classes={{ paper: classes.drawerPaper }}
        >
          <div className={classes.drawerHeader}>
            <Tooltip title="Close menu">
              <IconButton className={classes.menuButton} aria-label="close menu" onClick={handleDrawerClose}>
                <ChevronLeftIcon />
              </IconButton>
            </Tooltip>
          </div>
          <Divider />
          <p>Options go here</p>
          <button onClick={handleGetMapClick}>Get Map</button>
        </Drawer>
      </nav>

      <main 
        id="maincontent" 
        tabIndex="-1"
        className={clsx(classes.content, { [classes.contentShift]: open, })}>
        <div className={classes.drawerHeader} />

        {getMap &&
          <GetImage />
        }

      </main>
    </>
  )
}

export default Main;
