import React, { useState, useContext } from 'react';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';


const monthChoices = ['January','February','March','April','May','June','July','August','September','October','November','December'];
const dayChoices = Array.from({length: 31}, (v, k) => k+1);

const useStyles = makeStyles((theme) => ({
	filterLabel: {
		display: "inline-block",
		lineHeight: "4.4em",
		verticalAlign: "bottom",
		marginRight: "0.5em",
	},
	filterGroup: {
		display: "inline-block",
		border: "1px solid rgba(0, 0, 0, 0.23)",
		borderRadius: "4px",
		paddingTop: "0",
		paddingBottom: "0",
		margin: "0.6em 0 0.6em 0",
	},
	filterGroupLegend: {
		fontSize: "75%",
		color: "rgba(0, 0, 0, 0.54)",
		lineHeight: "1",
	},
}));

const DateRange = () => {
	const [fromFilterMonth, setFromFilterMonth] = useState();
	const [fromFilterDay, setFromFilterDay] = useState();
	const [toFilterMonth, setToFilterMonth] = useState();
	const [toFilterDay, setToFilterDay] = useState();
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	return (
		<div>
			<fieldset className={classes.filterGroup}>
				<legend className={classes.filterGroupLegend}>Interval start date</legend>
				<TextField
				select
				id="fromFilterMonth"
				value={fromFilterMonth}
				onChange={this.handleChange('fromFilterMonth')}
				className={classes.textfield}
				margin="dense"
				SelectProps={{ native: false }}
				>
				{this.serialStringSelectOptions(monthChoices)}
				</TextField>
				<TextField
				select
				id="fromFilterDay"
				value={fromFilterDay}
				onChange={this.handleChange('fromFilterDay')}
				margin="dense"
				SelectProps={{ native: false }}
				>
				{this.serialStringSelectOptions(dayChoices)}
				</TextField>
				</fieldset>
				<Typography className={classes.filterLabel} style={{marginLeft:"0.5em"}}>
				through
				</Typography>
				<fieldset className={classes.filterGroup}>
				<legend className={classes.filterGroupLegend}>Interval end date</legend>
				<TextField
				select
				id="toFilterMonth"
				value={toFilterMonth}
				onChange={this.handleChange('toFilterMonth')}
				className={classes.textfield}
				margin="dense"
				SelectProps={{ native: false }}
				>
				{this.serialStringSelectOptions(monthChoices)}
				</TextField>
				<TextField
				select
				id="toFilterDay"
				value={toFilterDay}
				onChange={this.handleChange('toFilterDay')}
				margin="dense"
				SelectProps={{ native: false }}
				>
				{this.serialStringSelectOptions(dayChoices)}
				</TextField>
			</fieldset>
		</div>
	)
}

export default DateRange;
