import React, { useEffect, useState, useContext } from 'react';
import { MapContainer, TileLayer, Polygon, ImageOverlay } from 'react-leaflet'
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';
import { multiStateBbox } from '../utilities/utils';
import MapTitle from './MapTitle';

const useStyles = makeStyles((theme) => ({
	nav: {
		position: "absolute",
		top: 0,
		left: 0,
		padding: "3px",
	},
	mapContainer: {
		border: "1pt solid midnightblue",
		height: window.innerHeight * 0.6,
	},
	mapTitleContainer: {
		borderLeft: "1pt solid midnightblue",
		borderRight: "1pt solid midnightblue",
		borderBottom: "1pt solid midnightblue",
		padding: "0.5em",
		marginBottom: "1em",
	},
}));

const LeafletMap = (props) => {
	const { imgsrc, submittedParams } = props;
	const [ bbox, setBbox ] = useState();
	const [ imgcoords, setImgcoords ] = useState();
	const [ resultsError, setResultsError ] = useState();
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	const fetchBbox = postals => {
		const jsonparams = JSON.stringify({state: postals, meta: "bbox"});
		fetch("https://data.rcc-acis.org/General/state", {body: jsonparams, method: "POST", headers: {'Content-Type': 'application/json'}})
			.then(response => {
				if (!response.ok) {
					setResultsError("Error");
				}
				return response.json();
			})
			.then(response => {
				if (response.meta && response.meta.length) {
					const bbox = multiStateBbox(response.meta);
					setBbox(bbox);
				} else {
					setResultsError("No bbox for "+postals);
				}
			})
			.catch(err => {
				if (err.message.includes("Failed to execute 'createObjectURL' on 'URL'")) {
					setResultsError("Invalid request");
				} else {
					setResultsError(err.message);
				};
			})
	};

	useEffect(() => {
		if (inputContext.inputParams.areaDef.bbox) {
			const bbox = JSON.parse(submittedParams).bbox;
			setBbox([parseFloat(bbox[0]),parseFloat(bbox[1]),parseFloat(bbox[2]),parseFloat(bbox[3])]);
		} else {
			const postals = inputContext.inputParams.areaDef.state;
			fetchBbox(postals);
		}
		// eslint-disable-next-line 
	}, [inputContext.inputParams.areaDef]);

	useEffect(() => {
		if (bbox) {
			// convert bbox to Leaflet coordinates
			setImgcoords([ [bbox[3],bbox[0]], [bbox[3],bbox[2]], [bbox[1],bbox[2]], [bbox[1],bbox[0]] ]);
		}
		// eslint-disable-next-line 
	}, [bbox]);


	return (
		<>
			<div className={classes.mapContainer}>
				{bbox && 
					<MapContainer 
						bounds={[[bbox[1], bbox[0]], [bbox[3], bbox[2]]]}
						zoomControl={true}
						scrollWheelZoom={true}
						attributionControl={true}
					>
						<TileLayer
							url='https://{s}.basemaps.cartocdn.com/light_nolabels/{z}/{x}/{y}{r}.png'
							attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>'
						/>

						{imgcoords &&
							<Polygon 
								pathOptions={{color:"lightgray", weight:1, fill:false, opacity:0.8}}
								positions={imgcoords} 
							/>
						}

						{imgcoords && imgsrc &&
							<ImageOverlay
								url={imgsrc}
								bounds={imgcoords}
								opacity={0.8}
								zIndex={10}
							/>
						}

					</MapContainer>
				}
			</div>
			<div className={classes.mapTitleContainer}>
				<MapTitle 
					submittedParams={submittedParams}
				/>
			</div>
			{resultsError &&
				<p>Error: {resultsError}</p>
			}
		</>
	);
}

export default LeafletMap;
