import React, {useContext} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputParamsContext from './InputParamsContext';

const options = [
	{menuName: 'Maximum temperature', name: 'maxt', reduce: 'mean'},	//set as default in Main
	{menuName: 'Minimum temperature', name: 'mint', reduce: 'mean'},
	{menuName: 'Average temperature', name: 'avgt', reduce: 'mean'},
	{menuName: 'Precipitation', name: 'pcpn', reduce: 'sum'},
	{menuName: 'Snowfall', name: 'snow', reduce: 'sum',},
	{menuName: 'Heating Degree Days', name: 'hdd', reduce: 'sum',},
	{menuName: 'Cooling Degree Days', name: 'cdd', reduce: 'sum',},
	{menuName: 'Growing Degree Days', name: 'gdd', reduce: 'sum',},
];

const useStyles = makeStyles((theme) => ({
	listContainer: {
		border: "1pt solid midnightblue",
	},
}));

const ElementSelect = () => {
	const [anchorEl, setAnchorEl] = React.useState(null);
	const [selectedIndex, setSelectedIndex] = React.useState(0);
	const inputContext = useContext(InputParamsContext);
	const classes = useStyles();

	const handleClickListItem = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleMenuItemClick = (event, index) => {
		setSelectedIndex(index);
		setAnchorEl(null);
		const newElems = inputContext.inputParams.elems;
		newElems[0] = {...newElems[0], name: options[index].name, reduce: options[index].reduce}
		inputContext.updateInputParams({elems: newElems});
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	return (
		<div>
			<List className={classes.listContainer} aria-label="Element selection">
				<ListItem
					button
					aria-haspopup="true"
					aria-controls="element-menu"
					aria-label="selected element"
					onClick={handleClickListItem}
				>
					<ListItemText primary="Click to select element" secondary={"Currently selected: "+options[selectedIndex].menuName} />
				</ListItem>
			</List>
			<Menu
				id="element-menu"
				anchorEl={anchorEl}
				keepMounted
				open={Boolean(anchorEl)}
				onClose={handleClose} 
			>
				{options.map((option, index) => (
					<MenuItem
						key={option.menuName}
						selected={index === selectedIndex}
						onClick={(event) => handleMenuItemClick(event, index)}
					>
						{option.menuName}
					</MenuItem>
				))}
			</Menu>
		</div>
	);
}

export default ElementSelect;
