import React, { useEffect, useRef } from "react";
import Button from '@material-ui/core/Button';

const DisplayMap = (props) => {
	const {imgsrc} = props;
	const imgRef = useRef();

	useEffect(() => {
		imgRef.current.focus();
	});

	return (
		<>
			<img src={imgsrc} className="mapImage" ref={imgRef} tabIndex="0" alt="requested map" />
			<Button variant="outlined">Download image (png)</Button>
			<Button variant="outlined">Download image (geotiff)</Button>
			<Button variant="outlined">Download data (json)</Button>
		</>
	);
}

export default DisplayMap;
